'use strict';

require('./yqj-enhanced-require').useGlobal('yqj_require');

require('./yqj-logger').useGlobal({
  // forConsole can specify multi config
  forConsole: [{
    name: 'log',
    level: 'log',
  }, {
    name: 'logw',
    level: 'warn',
  }],
  // just one
  forFile: {
    name: 'flog',
    level: 'log',
  },
  forError: 'elog',
});

require('./yqj-debugger').useGlobal('Debugger');

